var http  = require('http'),
    proxy = require('../').proxy;

var to_port   = process.argv[2] || 3000;
var from_port = process.argv[3] || 9000;

var handler = proxy.setup({
  debug: true,
  backend: {
    host: 'localhost',
    port: to_port
  }
})

http.createServer(handler).listen(from_port);
console.log('Proxying from ' + from_port + ' to ' + to_port);
